import { Injectable } from '@angular/core';
import { XmlBtmService } from './xml-btm.service';
import { FileConverterService } from './file-converter.service';
import { CsvFileConverterService } from './csv-file-converter.service';
import { CsvOptions } from './csv-converter.service';
import { firstValueFrom, throwError } from 'rxjs';
import { timeout, catchError } from 'rxjs/operators';

export interface OrchestratedResult {
    result: any[];         // flat tablo datası
    properties: string[];  // tablo başlıkları
    prettyJson?: any;      // okunaklı/gruplu JSON (BTM başarılıysa)
    via: 'btm' | 'local';  // hangi yol kullanıldı
}

@Injectable({ providedIn: 'root' })
export class BtmOrchestratorService {
    constructor(
        private xmlBtm: XmlBtmService,
        private fileConverter: FileConverterService,
        private csvFileConverter: CsvFileConverterService
    ) { }

    /**
     * Tek sorumluluk: XML dosyasını al, BTM'yi dene, olmazsa local parser'a düş,
     * sonucu tablo (flat) + opsiyonel prettyJson ile döndür.
     */
    async processXml(file: File, csvOptions?: CsvOptions): Promise<OrchestratedResult> {
        const xmlText = await this.readFileAsText(file);

        // 1) BTM'yi dene (2 sn timeout)
        try {
            const csvString: string = await firstValueFrom(
                this.xmlBtm.uploadXml(xmlText).pipe(
                    timeout(2000),
                    catchError(err => throwError(() => err))
                )
            );
            
            console.log('BTM CSV çıktısı:', csvString);

            // Only detect delimiter for options
            const delimiter = this.detectDelimiter(csvString);
            
            // Always use CSV converter service for consistency
            const defaultOptions: CsvOptions = {
                hasHeader: false,  // BTM Type/Key/Value kolonlar için header false
                skipEmptyLines: true,
                selectedDelimiter: delimiter,
                doubleQuoteWrap: false,
                selectedRowDelimiter: 'newline',
                rowPrefix: '',
                rowSuffix: '',
                selectedEncoding: 'utf-8',
                selectedQuoteOption: 'none',
                trimWhitespace: true,
            };

            // BTM için her zaman header false ve algılanan delimiter kullan
            const options: CsvOptions = {
                ...(csvOptions || defaultOptions),
                hasHeader: false,
                selectedDelimiter: delimiter
            };

            // CSV servisi ile dönüşüm
            const csvFile = new File([csvString], 'btm.csv', { type: 'text/csv' });
            const converterResult = await this.csvFileConverter.convert(csvFile, options);
            console.log('CSV converter returned:', converterResult);
            
            // Type/Key/Value format için özel dönüşüm - tüm muhtemel varyasyonlar için
            const records: Array<Record<string, any>> = converterResult.result || [];
            
            // 1) İlk birkaç satıra bakarak Type/Key/Value formatı mı algıla
            let isTypeKeyValue = false;
            let typeField = '';
            let keyField = '';
            let valueField = '';
            
            // Farklı format olasılıkları
            const possibleFormats = [
                { type: 'column1', key: 'column2', value: 'column3' }, // header: false -> column1,2,3...
                { type: 'Type', key: 'Key', value: 'Value' },          // header: true -> Type,Key,Value
                { type: 'type', key: 'key', value: 'value' },          // header: true -> type,key,value
                { type: '0', key: '1', value: '2' }                    // header: false -> indeks bazlı
            ];
            
            for (const format of possibleFormats) {
                // Bu format için gereken alanlar var mı?
                const hasTypeField = records.length > 0 && records[0][format.type] !== undefined;
                const hasKeyField = records.length > 0 && records[0][format.key] !== undefined;
                const hasValueField = records.length > 0 && records[0][format.value] !== undefined;
                
                if (hasTypeField && hasKeyField && hasValueField) {
                    // İlk birkaç satırda "Parameters", "Header" gibi tip değerleri var mı?
                    const typeValues = new Set(['Parameters', 'Header', 'Details']);
                    const sampleRows = records.slice(0, Math.min(5, records.length));
                    const matchingTypes = sampleRows.filter((r: Record<string, any>) => typeValues.has(r[format.type]));
                    
                    if (matchingTypes.length > 0) {
                        isTypeKeyValue = true;
                        typeField = format.type;
                        keyField = format.key;
                        valueField = format.value;
                        break;
                    }
                }
            }
            
            // Type/Key/Value format algılandıysa, gruplayıp dönüştür
            if (isTypeKeyValue) {
                // Her kayıt grubu için saklanan key/value çiftleri
                const recordGroups: Record<string, any>[] = [];
                
                // Ortak Parameters ve Header kayıtları
                const sharedRecords: Record<string, string> = {};
                const detailsRecords: Array<{type: string, key: string, value: string}> = [];
                
                // Önce tüm Parameters ve Header kayıtlarını topla
                for (const row of records) {
                    const type = row[typeField];
                    const key = row[keyField];
                    const value = row[valueField] || '';
                    
                    if (type === 'Parameters' || type === 'Header') {
                        sharedRecords[key] = value;
                    } else if (type === 'Details') {
                        detailsRecords.push({ type, key, value });
                    }
                }
                
                // RegisterId'ye göre Detail kayıtlarını grupla
                // Eğer RegisterId yoksa, tüm Details tek bir grupta olacak
                const detailGroups: Array<{type: string, key: string, value: string}[]> = [];
                let currentGroup: Array<{type: string, key: string, value: string}> = [];
                
                for (const detail of detailsRecords) {
                    if (detail.key === 'RegisterId' && currentGroup.length > 0) {
                        detailGroups.push([...currentGroup]);
                        currentGroup = [];
                    }
                    currentGroup.push(detail);
                }
                
                if (currentGroup.length > 0) {
                    detailGroups.push(currentGroup);
                }
                
                // Her grup için flat object oluştur
                if (detailGroups.length > 0) {
                    // Birden çok grup varsa, her biri için ayrı flat object
                    for (const group of detailGroups) {
                        const flatObject = { ...sharedRecords };
                        
                        for (const detail of group) {
                            flatObject[detail.key] = detail.value;
                        }
                        
                        recordGroups.push(flatObject);
                    }
                } else {
                    // Sadece ortak kayıtlar varsa, tek flat object
                    recordGroups.push({ ...sharedRecords });
                }
                
                // Tüm flat objelerin birleşik özellikleri
                const allProperties = Array.from(
                    new Set(recordGroups.flatMap(obj => Object.keys(obj)))
                );
                
                console.log('BTM Type/Key/Value formatı algılandı. Gruplar oluşturuldu:', 
                             recordGroups);
                
                return {
                    result: recordGroups,
                    properties: allProperties,
                    prettyJson: recordGroups,
                    via: 'btm',
                };
            }

            // Normal CSV
            return {
                ...converterResult,
                prettyJson: converterResult.result,
                via: 'btm',
            };
        } catch (_btmErr) {
            // 4) BTM başarısızsa local converter'a düş
            const converter = this.fileConverter.getConverter('xml');
            if (!converter) throw new Error('XML dönüştürücü bulunamadı (local).');

            const localResult = await converter.convert(file, {
                // UI'dan opsiyon geliyorsa burada parametrik verebilirsin
            });

            // localResult zaten { result, properties } şemasında
            return { ...localResult, via: 'local' };
        }
    }

    // --------- Helpers (servise taşındı) ---------

    private readFileAsText(file: File): Promise<string> {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.onload = () => resolve(reader.result as string);
            reader.onerror = (err) => reject(err);
            reader.readAsText(file);
        });
    }

    // Bu fonksiyonlar artık kullanılmadığı için kaldırıldı - CSV dönüşüm işlemleri CsvConverter servisine devredildi

    /**
     * Otomatik delimiter tespiti
     */
    private detectDelimiter(csvString: string): string {
        const lines = csvString.trim().split(/\r?\n/).slice(0, 5); // İlk 5 satırı kontrol et
        const delimiters = [',', ';', '\t', '|'];
        const counts: { [key: string]: number } = {};

        for (const delimiter of delimiters) {
            counts[delimiter] = 0;
            for (const line of lines) {
                // Quotes içindeki delimiter'ları sayma
                let inQuotes = false;
                for (let i = 0; i < line.length; i++) {
                    if (line[i] === '"') {
                        inQuotes = !inQuotes;
                    } else if (line[i] === delimiter && !inQuotes) {
                        counts[delimiter]++;
                    }
                }
            }
        }

        // En çok bulunan delimiter'ı döndür
        let maxCount = 0;
        let bestDelimiter = ',';
        for (const [delimiter, count] of Object.entries(counts)) {
            if (count > maxCount) {
                maxCount = count;
                bestDelimiter = delimiter;
            }
        }

        console.log('Delimiter tespiti:', counts, 'Seçilen:', bestDelimiter);
        return bestDelimiter;
    }

    /**
     * BTM'den gelen Type/Key/Value formatını düz objeye çevir
     * Bu fonksiyon CSV dönüşüm sonucunda Type/Key/Value kolonları varsa kullanılır.
     * Normal CSV dönüşümü için gereksizdir.
     */
    private transformBtmJson(typedRows: Array<{ Type?: string; Key?: string; Value?: string }>) {
        // Tüm Key/Value çiftlerini tek bir objeye topla
        const result: Record<string, string> = {};
        
        for (const row of typedRows || []) {
            const { Key, Value } = row || {};
            // Her Key/Value çiftini direkt objeye ekle
            if (Key && Value !== undefined) {
                result[Key] = Value;
            }
        }

        return result;
    }
}
