import { Component, EventEmitter, Output } from '@angular/core';
import { CsvFileConverterService } from 'src/app/services/csv-file-converter.service';
import { TxtFileConverterService } from 'src/app/services/txt-file-converter.service';
import { CsvOptions } from 'src/app/services/csv-converter.service';
import { TxtToJsonOptions } from 'src/app/services/txt-to-json.service';

@Component({
    selector: 'app-file-uploader',
    templateUrl: './file-uploader.component.html',
    styleUrls: ['./file-uploader.component.scss'],
})
export class FileUploaderComponent {
    @Output() onConvert = new EventEmitter<any>();
    @Output() onError = new EventEmitter<string>();
    @Output() onFileClear = new EventEmitter<void>();
    @Output() onOptionsChange = new EventEmitter<any>();

    selectedFile: File | null = null;
    fileType: 'csv' | 'txt' | null = null;
    isProcessing = false;

    // CSV options
    hasHeader = true;
    skipEmptyLines = true;
    selectedDelimiter = ',';
    doubleQuoteWrap = true;
    selectedRowDelimiter = 'newline';
    rowPrefix = '';
    rowSuffix = '';
    selectedEncoding = 'utf-8';
    selectedQuoteOption: 'none' | 'single' | 'double' = 'none';
    trimWhitespace = true;

    // TXT options
    fieldCount = 3;
    selectedFieldIndex = 0;
    fieldConfigs = [
        { start: 0, length: 10 },
        { start: 10, length: 10 },
        { start: 20, length: 10 },
    ];

    delimiterOptions = [
        { value: ',', label: 'Comma (,)' },
        { value: ';', label: 'Semicolon (;)' },
        { value: '|', label: 'Pipe (|)' },
        { value: ':', label: 'Colon (:)' },
        { value: '\t', label: 'Tab' },
        { value: '/', label: 'Slash (/)' },
        { value: '#', label: 'Hash (#)' },
    ];

    rowDelimiterOptions = [
        { value: 'newline', label: 'Newline (\\n)' },
        { value: 'carriage-return', label: 'Carriage Return (\\r)' },
        { value: 'crlf', label: 'CR + LF (\\r\\n)' },
        { value: ',', label: 'Comma (,)' },
        { value: ';', label: 'Semicolon (;)' },
        { value: '|', label: 'Pipe (|)' },
        { value: ':', label: 'Colon (:)' },
        { value: '\t', label: 'Tab' },
        { value: '/', label: 'Slash (/)' },
        { value: '#', label: 'Hash (#)' },
    ];

    quoteOptions = [
        { value: 'none', label: 'None' },
        { value: 'single', label: "Single Quote (')" },
        { value: 'double', label: 'Double Quote (")' },
    ];

    encodingOptions = [
        { value: 'utf-8', label: 'UTF-8' },
        { value: 'utf-8-bom', label: 'UTF-8 with BOM' },
        { value: 'windows-1254', label: 'Windows-1254 (Turkish)' },
        { value: 'iso-8859-9', label: 'ISO-8859-9 (Turkish)' },
    ];

    constructor(
        private csvConverter: CsvFileConverterService,
        private txtConverter: TxtFileConverterService
    ) { }

    onFileSelect(event: any): void {
        const files = event.target.files;
        if (files && files.length > 0) {
            this.selectedFile = files[0];
            this.fileType = null;
        }
    }

    clearSelection(): void {
        this.selectedFile = null;
        this.fileType = null;
        const fileInput = document.getElementById('csvFileInput') as HTMLInputElement;
        if (fileInput) fileInput.value = '';
        this.onFileClear.emit();
    }

    getCsvOptions(): CsvOptions {
        return {
            hasHeader: this.hasHeader,
            skipEmptyLines: this.skipEmptyLines,
            selectedDelimiter: this.selectedDelimiter,
            doubleQuoteWrap: this.doubleQuoteWrap,
            selectedRowDelimiter: this.selectedRowDelimiter,
            rowPrefix: this.rowPrefix,
            rowSuffix: this.rowSuffix,
            selectedEncoding: this.selectedEncoding,
            selectedQuoteOption: this.selectedQuoteOption,
            trimWhitespace: this.trimWhitespace,
        };
    }

    getTxtOptions(): TxtToJsonOptions {
        return {
            fieldCount: this.fieldCount,
            startPositions: this.fieldConfigs.map(c => c.start),
            lengths: this.fieldConfigs.map(c => c.length),
            hasHeader: this.hasHeader,
            skipEmptyLines: this.skipEmptyLines,
        };
    }

    async processData(): Promise<void> {
        if (!this.selectedFile || !this.fileType) {
            this.onError.emit('Dosya ve tipi seçilmelidir.');
            return;
        }

        this.isProcessing = true;
        let result: any;

        try {
            if (this.fileType === 'csv') {
                const options = this.getCsvOptions();
                result = await this.csvConverter.convert(this.selectedFile, options);
                this.onOptionsChange.emit(options);
            } else if (this.fileType === 'txt') {
                const options = this.getTxtOptions();
                result = await this.txtConverter.convert(this.selectedFile, options);
                this.onOptionsChange.emit(options);
            } else {
                this.onError.emit('Geçersiz dosya tipi');
                return;
            }

            this.onConvert.emit(result);
        } catch (error) {
            this.onError.emit('Hata: ' + error);
        } finally {
            this.isProcessing = false;
        }
    }

    // Change triggers
    private emitOptions(): void {
        const options = this.getCsvOptions();
        this.onOptionsChange.emit(options);
    }

    onHeaderCheckboxChange(): void {
        this.emitOptions();
    }

    onSkipEmptyLinesChange(): void {
        this.emitOptions();
    }

    onFieldCountChange(): void {
        const count = this.fieldCount;
        while (this.fieldConfigs.length < count) {
            const last = this.fieldConfigs[this.fieldConfigs.length - 1];
            const newStart = last ? last.start + last.length : 0;
            this.fieldConfigs.push({ start: newStart, length: 10 });
        }
        while (this.fieldConfigs.length > count) {
            this.fieldConfigs.pop();
        }
    }

    onFieldConfigChange(): void { }

    onDelimiterChange(): void {
        this.emitOptions();
    }

    onRowDelimiterChange(): void {
        this.emitOptions();
    }

    onQuoteOptionChange(): void {
        this.doubleQuoteWrap = this.selectedQuoteOption === 'double';
        this.emitOptions();
    }

    onEncodingChange(): void {
        this.emitOptions();
    }

    onRowPrefixChange(): void {
        this.emitOptions();
    }

    onRowSuffixChange(): void {
        this.emitOptions();
    }

    onTrimWhitespaceChange(): void {
        this.emitOptions();
    }

    onDoubleQuoteWrapChange(): void {
        this.selectedQuoteOption = this.doubleQuoteWrap ? 'double' : 'none';
        this.emitOptions();
    }
}
