    <input id="csvFileInput" type="file" (change)="onFileSelect($event)" />

    <div *ngIf="selectedFile">
        <label>Dosya Türü:</label>
        <select [(ngModel)]="fileType">
            <option [ngValue]="'csv'">CSV</option>
            <option [ngValue]="'txt'">TXT</option>
        </select>

        <div *ngIf="fileType === 'csv'">
            <h4>CSV Ayarları</h4>
            <label>
                <input type="checkbox" [(ngModel)]="hasHeader" (change)="onHeaderCheckboxChange()" /> Başlık Var
            </label>
            <label>
                <input type="checkbox" [(ngModel)]="skipEmptyLines" (change)="onSkipEmptyLinesChange()" /> Boş Satırları Atla
            </label>
            <label>
                Ayırıcı:
                <select [(ngModel)]="selectedDelimiter" (change)="onDelimiterChange()">
                    <option *ngFor="let option of delimiterOptions" [value]="option.value">{{ option.label }}</option>
                </select>
            </label>
            <label>
                Satır Sonu Ayırıcı:
                <select [(ngModel)]="selectedRowDelimiter" (change)="onRowDelimiterChange()">
                    <option *ngFor="let option of rowDelimiterOptions" [value]="option.value">{{ option.label }}</option>
                </select>
            </label>
            <label>
                Tırnak Opsiyonu:
                <select [(ngModel)]="selectedQuoteOption" (change)="onQuoteOptionChange()">
                    <option *ngFor="let option of quoteOptions" [value]="option.value">{{ option.label }}</option>
                </select>
            </label>
            <label>
                Encoding:
                <select [(ngModel)]="selectedEncoding" (change)="onEncodingChange()">
                    <option *ngFor="let option of encodingOptions" [value]="option.value">{{ option.label }}</option>
                </select>
            </label>
            <label>
                Satır Prefix: <input type="text" [(ngModel)]="rowPrefix" (input)="onRowPrefixChange()" />
            </label>
            <label>
                Satır Suffix: <input type="text" [(ngModel)]="rowSuffix" (input)="onRowSuffixChange()" />
            </label>
            <label>
                <input type="checkbox" [(ngModel)]="trimWhitespace" (change)="onTrimWhitespaceChange()" /> Boşlukları Kırp
            </label>
            <label>
                <input type="checkbox" [(ngModel)]="doubleQuoteWrap" (change)="onDoubleQuoteWrapChange()" /> Çift Tırnakla Sar
            </label>
        </div>

        <div *ngIf="fileType === 'txt'">
            <h4>TXT Ayarları</h4>
            <label>
                <input type="checkbox" [(ngModel)]="hasHeader" (change)="onHeaderCheckboxChange()" /> Başlık Var
            </label>
            <label>
                <input type="checkbox" [(ngModel)]="skipEmptyLines" (change)="onSkipEmptyLinesChange()" /> Boş Satırları Atla
            </label>
            <label>
                Alan Sayısı:
                <input type="number" [(ngModel)]="fieldCount" (input)="onFieldCountChange()" min="1" />
            </label>
            <div *ngFor="let field of fieldConfigs; let i = index">
                <label>
                    Başlangıç:
                    <input type="number" [(ngModel)]="fieldConfigs[i].start" (input)="onFieldConfigChange()" />
                </label>
                <label>
                    Uzunluk:
                    <input type="number" [(ngModel)]="fieldConfigs[i].length" (input)="onFieldConfigChange()" />
                </label>
            </div>
        </div>

        <button (click)="processData()" [disabled]="isProcessing">Dönüştür</button>
        <button (click)="clearSelection()">Temizle</button>
    </div>
</div>
