import { Injectable } from '@angular/core';
import { XmlBtmService } from './xml-btm.service';
import { FileConverterService } from './file-converter.service';
import { CsvFileConverterService } from './csv-file-converter.service';
import { CsvOptions } from './csv-converter.service';
import { firstValueFrom, throwError } from 'rxjs';
import { timeout, catchError } from 'rxjs/operators';

export interface OrchestratedResult {
    result: any[];         // flat tablo datası
    properties: string[];  // tablo başlıkları
    prettyJson?: any;      // okunaklı/gruplu JSON (BTM başarılıysa)
    via: 'btm' | 'local';  // hangi yol kullanıldı
}

@Injectable({ providedIn: 'root' })
export class BtmOrchestratorService {
    constructor(
        private xmlBtm: XmlBtmService,
        private fileConverter: FileConverterService,
        private csvFileConverter: CsvFileConverterService
    ) { }

    /**
     * Tek sorumluluk: XML dosyasını al, BTM'yi dene, olmazsa local parser'a düş,
     * sonucu tablo (flat) + opsiyonel prettyJson ile döndür.
     */
    async processXml(file: File, csvOptions?: CsvOptions): Promise<OrchestratedResult> {
        const xmlText = await this.readFileAsText(file);

        // 1) BTM'yi dene (2 sn timeout)
        try {
            const csvString: string = await firstValueFrom(
                this.xmlBtm.uploadXml(xmlText).pipe(
                    timeout(2000),
                    catchError(err => throwError(() => err))
                )
            );
            
            console.log('BTM CSV çıktısı:', csvString);

            // Detect delimiter
            const delimiter = this.detectDelimiter(csvString);

            // Parse CSV lines into arrays using internal parser
            const parsedRows = this.parseCsvString(csvString, delimiter);

            // Heuristic: if many rows start with Type values (Parameters/Header/Details),
            // then BTM returned Type/Key/Value format and we should transform to flat JSON.
            const typeNames = new Set(['Parameters', 'Header', 'Details']);
            const typedFormatCount = parsedRows.filter(r => r.length >= 2 && typeNames.has(r[0])).length;
            const typedFormat = typedFormatCount >= Math.max(1, Math.floor(parsedRows.length / 4));

            if (typedFormat) {
                // Convert parsedRows to TypedRecord[]
                interface TypedRecord {
                    Type: string;
                    Key: string;
                    Value: string;
                }

                const typedRecords: TypedRecord[] = parsedRows.map(r => ({
                    Type: (r[0] || '').trim(),
                    Key: (r[1] || '').trim(),
                    Value: (r[2] || '').trim(),
                })).filter(t => t.Type && t.Key);

                // Group Details into records per RegisterId, keeping shared Parameters/Header
                const recordGroups: TypedRecord[][] = [];
                let currentGroup: TypedRecord[] = [];
                let hasFoundDetails = false;

                const sharedRecords = typedRecords.filter(r => r.Type === 'Parameters' || r.Type === 'Header');

                typedRecords.forEach(record => {
                    if (record.Type === 'Details') {
                        hasFoundDetails = true;
                        if (record.Key === 'RegisterId' && currentGroup.length > 0) {
                            recordGroups.push([...sharedRecords, ...currentGroup]);
                            currentGroup = [];
                        }
                        currentGroup.push(record);
                    }
                });

                if (currentGroup.length > 0) {
                    recordGroups.push([...sharedRecords, ...currentGroup]);
                }

                if (!hasFoundDetails && sharedRecords.length > 0) {
                    recordGroups.push(sharedRecords);
                }

                // Transform each group to flat object
                const rowsObjects = recordGroups.map(g => this.transformBtmJson(g));
                const properties = Array.from(new Set(rowsObjects.flatMap(obj => Object.keys(obj))));

                return {
                    result: rowsObjects,
                    properties: properties.length > 0 ? properties : ['Type', 'Key', 'Value'],
                    prettyJson: rowsObjects,
                    via: 'btm',
                };
            }
            // Use provided CSV options from UI when available, otherwise fall back to UI defaults
            const defaultOptions: CsvOptions = {
                hasHeader: true,
                skipEmptyLines: true,
                selectedDelimiter: delimiter,
                doubleQuoteWrap: true,
                selectedRowDelimiter: 'newline',
                rowPrefix: '',
                rowSuffix: '',
                selectedEncoding: 'utf-8',
                selectedQuoteOption: 'none',
                trimWhitespace: true,
            };

            // If caller provided a selectedDelimiter, keep it; otherwise use detected delimiter
            const options = csvOptions
                ? { ...csvOptions, selectedDelimiter: csvOptions.selectedDelimiter && csvOptions.selectedDelimiter.trim() !== '' ? csvOptions.selectedDelimiter : delimiter }
                : defaultOptions;

            // Create an in-memory File object and hand it to the csv converter
            const csvFile = new File([csvString], 'btm.csv', { type: 'text/csv' });
            const converterResult = await this.csvFileConverter.convert(csvFile, options);
            console.log('CSV converter returned:', converterResult);

            // Return the converter's result directly and mark via 'btm'
            return {
                ...converterResult,
                prettyJson: converterResult.result,
                via: 'btm',
            };
        } catch (_btmErr) {
            // 4) BTM başarısızsa local converter'a düş
            const converter = this.fileConverter.getConverter('xml');
            if (!converter) throw new Error('XML dönüştürücü bulunamadı (local).');

            const localResult = await converter.convert(file, {
                // UI'dan opsiyon geliyorsa burada parametrik verebilirsin
            });

            // localResult zaten { result, properties } şemasında
            return { ...localResult, via: 'local' };
        }
    }

    // --------- Helpers (servise taşındı) ---------

    private readFileAsText(file: File): Promise<string> {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.onload = () => resolve(reader.result as string);
            reader.onerror = (err) => reject(err);
            reader.readAsText(file);
        });
    }

    /**
     * Geliştirilmiş CSV parser - quoted değerleri ve farklı delimiter'ları destekler
     */
    private parseCsvString(csvString: string, delimiter: string = ','): string[][] {
        const lines = csvString.trim().split(/\r?\n/);
        const result: string[][] = [];

        for (const line of lines) {
            if (!line.trim()) continue; // Boş satırları atla
            
            const row = this.parseCsvLine(line, delimiter);
            result.push(row);
        }

        return result;
    }

    /**
     * Tek bir CSV satırını parse eder - quoted değerleri ve delimiter'ları düzgün işler
     */
    private parseCsvLine(line: string, delimiter: string = ','): string[] {
        const result: string[] = [];
        let current = '';
        let inQuotes = false;
        let i = 0;

        while (i < line.length) {
            const char = line[i];
            const nextChar = line[i + 1];

            if (char === '"') {
                if (inQuotes && nextChar === '"') {
                    // Escaped quote ("")
                    current += '"';
                    i += 2;
                } else {
                    // Toggle quote state
                    inQuotes = !inQuotes;
                    i++;
                }
            } else if (char === delimiter && !inQuotes) {
                // Field separator outside of quotes
                result.push(current.trim());
                current = '';
                i++;
            } else {
                // Regular character
                current += char;
                i++;
            }
        }

        // Add the last field
        result.push(current.trim());

        // Clean up quotes from values if needed
        return result.map(value => {
            // Remove surrounding quotes if present
            if (value.startsWith('"') && value.endsWith('"')) {
                return value.slice(1, -1);
            }
            return value;
        });
    }

    /**
     * Otomatik delimiter tespiti
     */
    private detectDelimiter(csvString: string): string {
        const lines = csvString.trim().split(/\r?\n/).slice(0, 5); // İlk 5 satırı kontrol et
        const delimiters = [',', ';', '\t', '|'];
        const counts: { [key: string]: number } = {};

        for (const delimiter of delimiters) {
            counts[delimiter] = 0;
            for (const line of lines) {
                // Quotes içindeki delimiter'ları sayma
                let inQuotes = false;
                for (let i = 0; i < line.length; i++) {
                    if (line[i] === '"') {
                        inQuotes = !inQuotes;
                    } else if (line[i] === delimiter && !inQuotes) {
                        counts[delimiter]++;
                    }
                }
            }
        }

        // En çok bulunan delimiter'ı döndür
        let maxCount = 0;
        let bestDelimiter = ',';
        for (const [delimiter, count] of Object.entries(counts)) {
            if (count > maxCount) {
                maxCount = count;
                bestDelimiter = delimiter;
            }
        }

        console.log('Delimiter tespiti:', counts, 'Seçilen:', bestDelimiter);
        return bestDelimiter;
    }

    // BTM'den gelen Type/Key/Value formatını düz objeye çevir
    private transformBtmJson(flatData: Array<{ Type: string; Key: string; Value: string }>) {
        // Tüm Key/Value çiftlerini tek bir objeye topla
        const result: any = {};
        
        for (const row of flatData || []) {
            const { Key, Value } = row || ({} as any);
            // Her Key/Value çiftini direkt objeye ekle
            if (Key && Value !== undefined) {
                result[Key] = Value;
            }
        }

        return result;
    }
}
