import { Injectable } from '@angular/core';
import { XmlBtmService } from './xml-btm.service';
import { FileConverterService } from './file-converter.service';
import { CsvFileConverterService } from './csv-file-converter.service';
import { CsvOptions } from './csv-converter.service';
import { firstValueFrom, throwError } from 'rxjs';
import { timeout, catchError } from 'rxjs/operators';

export interface OrchestratedResult {
    result: any[];         // flat tablo datası
    properties: string[];  // tablo başlıkları
    prettyJson?: any;      // okunaklı/gruplu JSON (BTM başarılıysa)
    via: 'btm' | 'local';  // hangi yol kullanıldı
}

@Injectable({ providedIn: 'root' })
export class BtmOrchestratorService {
    constructor(
        private xmlBtm: XmlBtmService,
        private fileConverter: FileConverterService,
        private csvFileConverter: CsvFileConverterService
    ) { }

    /**
     * Tek sorumluluk: XML dosyasını al, BTM'yi dene, olmazsa local parser'a düş,
     * sonucu tablo (flat) + opsiyonel prettyJson ile döndür.
     */
    async processXml(file: File, csvOptions?: CsvOptions): Promise<OrchestratedResult> {
        const xmlText = await this.readFileAsText(file);

        // 1) BTM'yi dene (2 sn timeout)
        try {
            const csvString: string = await firstValueFrom(
                this.xmlBtm.uploadXml(xmlText).pipe(
                    timeout(2000),
                    catchError(err => throwError(() => err))
                )
            );
            
            console.log('BTM CSV çıktısı:', csvString);

            // Only detect delimiter for options
            const delimiter = this.detectDelimiter(csvString);
            
            // Always use CSV converter service for consistency
            const defaultOptions: CsvOptions = {
                hasHeader: false,  // BTM Type/Key/Value kolonlar için header false
                skipEmptyLines: true,
                selectedDelimiter: delimiter,
                doubleQuoteWrap: false,
                selectedRowDelimiter: 'newline',
                rowPrefix: '',
                rowSuffix: '',
                selectedEncoding: 'utf-8',
                selectedQuoteOption: 'none',
                trimWhitespace: true,
            };

            // UI'dan gelen options ile merge et, ama delimiteri detect edilenle override et
            const options = csvOptions
                ? { ...csvOptions, selectedDelimiter: delimiter, hasHeader: false }
                : defaultOptions;

            // CSV servisi ile dönüşüm
            const csvFile = new File([csvString], 'btm.csv', { type: 'text/csv' });
            const converterResult = await this.csvFileConverter.convert(csvFile, options);
            console.log('CSV converter returned:', converterResult);
            
            // Type/Key/Value format için özel dönüşüm
            if (converterResult.properties.includes('Type') && 
                converterResult.properties.includes('Key') && 
                converterResult.properties.includes('Value')) {
                
                // Typed rows'u flat objects'e dönüştür
                const records = converterResult.result || [];
                const rowsObjects = [];
                const flatObject: Record<string, string> = {};
                
                for (const row of records) {
                    if (row.Type && row.Key) {
                        flatObject[row.Key] = row.Value || '';
                    }
                }
                
                if (Object.keys(flatObject).length > 0) {
                    rowsObjects.push(flatObject);
                }
                
                const properties = Object.keys(flatObject);
                
                return {
                    result: rowsObjects,
                    properties,
                    prettyJson: rowsObjects,
                    via: 'btm',
                };
            }

            // Normal CSV
            return {
                ...converterResult,
                prettyJson: converterResult.result,
                via: 'btm',
            };
        } catch (_btmErr) {
            // 4) BTM başarısızsa local converter'a düş
            const converter = this.fileConverter.getConverter('xml');
            if (!converter) throw new Error('XML dönüştürücü bulunamadı (local).');

            const localResult = await converter.convert(file, {
                // UI'dan opsiyon geliyorsa burada parametrik verebilirsin
            });

            // localResult zaten { result, properties } şemasında
            return { ...localResult, via: 'local' };
        }
    }

    // --------- Helpers (servise taşındı) ---------

    private readFileAsText(file: File): Promise<string> {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.onload = () => resolve(reader.result as string);
            reader.onerror = (err) => reject(err);
            reader.readAsText(file);
        });
    }

    // Bu fonksiyonlar artık kullanılmadığı için kaldırıldı - CSV dönüşüm işlemleri CsvConverter servisine devredildi

    /**
     * Otomatik delimiter tespiti
     */
    private detectDelimiter(csvString: string): string {
        const lines = csvString.trim().split(/\r?\n/).slice(0, 5); // İlk 5 satırı kontrol et
        const delimiters = [',', ';', '\t', '|'];
        const counts: { [key: string]: number } = {};

        for (const delimiter of delimiters) {
            counts[delimiter] = 0;
            for (const line of lines) {
                // Quotes içindeki delimiter'ları sayma
                let inQuotes = false;
                for (let i = 0; i < line.length; i++) {
                    if (line[i] === '"') {
                        inQuotes = !inQuotes;
                    } else if (line[i] === delimiter && !inQuotes) {
                        counts[delimiter]++;
                    }
                }
            }
        }

        // En çok bulunan delimiter'ı döndür
        let maxCount = 0;
        let bestDelimiter = ',';
        for (const [delimiter, count] of Object.entries(counts)) {
            if (count > maxCount) {
                maxCount = count;
                bestDelimiter = delimiter;
            }
        }

        console.log('Delimiter tespiti:', counts, 'Seçilen:', bestDelimiter);
        return bestDelimiter;
    }

    // BTM'den gelen Type/Key/Value formatını düz objeye çevir
    private transformBtmJson(flatData: Array<{ Type: string; Key: string; Value: string }>) {
        // Tüm Key/Value çiftlerini tek bir objeye topla
        const result: any = {};
        
        for (const row of flatData || []) {
            const { Key, Value } = row || ({} as any);
            // Her Key/Value çiftini direkt objeye ekle
            if (Key && Value !== undefined) {
                result[Key] = Value;
            }
        }

        return result;
    }
}
