import { Injectable } from '@angular/core';
import { CsvFileConverterService } from './csv-file-converter.service';
import { CsvOptions } from './csv-converter.service';
import { XmlBtmService } from './xml-btm.service';
import { FileConverterService } from './file-converter.service';
import { catchError, throwError, firstValueFrom, timeout } from 'rxjs';

export interface OrchestratedResult {
  result: any[];
  properties?: string[];
  prettyJson?: any;
  via: string;
  parametersData: Array<{ key: string; value: string }>;
  headerData: Array<{ key: string; value: string }>;
}

export interface BtmTransformResult {
  processedData: Array<Record<string, string>>;
  parametersData: Array<{ key: string; value: string }>;
  headerData: Array<{ key: string; value: string }>;
  rawRows: any[];
}

@Injectable({ providedIn: 'root' })
export class BtmOrchestratorService {
  constructor(
    private xmlBtm: XmlBtmService,
    private fileConverter: FileConverterService,
    private csvFileConverter: CsvFileConverterService
  ) {}

  /**
   * XML dosyasını alır:
   * 1) BTM'yi dener (CSV string alır)
   * 2) CSV'yi doğrudan CsvFileConverterService'e paslar (tüm dönüşüm orada)
   * 3) BTM başarısızsa local XML converter'a düşer
   */
  async processXml(file: File, csvOptions?: CsvOptions): Promise<OrchestratedResult> {
    const xmlText = await this.readFileAsText(file);

    // 1) BTM'yi dene (ör. 2 sn timeout)
    try {
      const csvString: string = await firstValueFrom(
        this.xmlBtm.uploadXml(xmlText).pipe(
          timeout(2000),
          catchError(err => throwError(() => err))
        )
      );

      console.log('BTM CSV:', csvString.slice(0, 200) + '...');

      // BTM çıktısını doğrudan kendi işleyicimizle dönüştür
      const btmResult = this.transformBtmCsv(csvString);

      // Eğer dönüşüm başarılıysa, dönüştürülmüş sonucu döndür
      if (btmResult.processedData.length > 0) {
        console.log('BTM dönüşümü başarılı:', btmResult.processedData.length, 'kayıt');

        // Flat tablo için gerekli property listesi
        const properties = [
          'DestinationIban', 'FinalAccountNo', 'TotalCount', 'OrderDetails',
          'TransferAmount', 'TotalAmount', 'RegisterId'
        ];

        // Parametre ve Header kayıtlarını da ekle
        btmResult.parametersData.forEach((p: { key: string; value: string }) => {
          if (!properties.includes(p.key)) {
            properties.push(p.key);
          }
        });

        btmResult.headerData.forEach((h: { key: string; value: string }) => {
          if (!properties.includes(h.key)) {
            properties.push(h.key);
          }
        });

        // Flat tablo sonucu oluştur
        const resultRows = btmResult.processedData.map((row: Record<string, string>) => {
          const fullRow: Record<string, string> = {};

          // Processed data alanlarını ekle
          for (const [key, value] of Object.entries(row)) {
            fullRow[key] = value || '-';
          }

          // Parameters alanlarını ekle
          for (const param of btmResult.parametersData) {
            if (!(param.key in fullRow)) {
              fullRow[param.key] = param.value || '-';
            }
          }

          // Header alanlarını ekle
          for (const header of btmResult.headerData) {
            if (!(header.key in fullRow)) {
              fullRow[header.key] = header.value || '-';
            }
          }

          return fullRow;
        });

        return {
          result: resultRows,
          properties,
          prettyJson: btmResult, // BTM sonucunun tamamını prettyJson olarak kaydet
          via: 'btm',
          parametersData: btmResult.parametersData,
          headerData: btmResult.headerData
        };
      }

      // Kendi dönüşümümüz başarısız olursa, eskisini deneyelim
      console.log('BTM kendi dönüşümü başarısız, standart dönüşüme geçiliyor');

      // CSV'yi HİÇ İŞLEMEDEN CSV converter'a ver
      const delimiter = this.detectDelimiter(csvString);
      const defaultOptions: CsvOptions = {
        hasHeader: false,                 // BTM çıktısı çoğu zaman header'sız geliyor
        skipEmptyLines: true,
        selectedDelimiter: delimiter,
        doubleQuoteWrap: false,
        selectedRowDelimiter: 'newline',
        rowPrefix: '',
        rowSuffix: '',
        selectedEncoding: 'utf-8',
        selectedQuoteOption: 'none',
        trimWhitespace: true,
      };
  const options: CsvOptions = { ...(csvOptions ?? defaultOptions), selectedDelimiter: delimiter };
  const csvFile = new File([csvString], 'btm.csv', { type: 'text/csv' });
  const converterResult = await this.csvFileConverter.convert(csvFile, options);

      return {
        result: converterResult.result,
        properties: converterResult.properties,
        prettyJson: converterResult.result,
        via: 'btm',
        parametersData: [], // Standart dönüşümde boş
        headerData: []      // Standart dönüşümde boş
      };
    } catch (_btmErr) {
      // 2) BTM başarısızsa local XML converter'a düş
      const converter = this.fileConverter.getConverter('xml');
      if (!converter) {
        throw new Error('XML dönüştürücü bulunamadı (local).');
      }

      const localResult = await converter.convert(file, {
        // UI'dan parametre gelirse burada forward edebilirsin
      });

      return {
        result: localResult.result,
        properties: localResult.properties,
        prettyJson: localResult.result,
        via: 'local',
        parametersData: [],
        headerData: []
      };
    }
  }

  // --------- Helpers ---------

  private readFileAsText(file: File): Promise<string> {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = (err) => reject(err);
      reader.readAsText(file);
    });
  }

  /** Basit delimiter tespiti (istersen CsvFileConverterService'e de devredebiliriz) */
  private detectDelimiter(csvString: string): string {
    const lines = csvString.trim().split(/\r?\n/).slice(0, 5);
    const delimiters = [',', ';', '\t', '|'];
    const counts: Record<string, number> = {};

    for (const d of delimiters) {
      counts[d] = 0;
      for (const line of lines) {
        let inQuotes = false;
        for (let i = 0; i < line.length; i++) {
          if (line[i] === '"') inQuotes = !inQuotes;
          else if (line[i] === d && !inQuotes) counts[d]++;
        }
      }
    }

    let best = ',', max = 0;
    for (const [d, c] of Object.entries(counts)) {
      if (c > max) { max = c; best = d; }
    }
    return best;
  }

  /**
   * BtmFrontTransformerService mantığına benzer dönüşüm
   * CSV string'i doğrudan dönüştürür
   */
  private transformBtmCsv(csv: string): BtmTransformResult {
    const rows = this.parseTypeKeyValueCsv(csv);

    console.log('Parsed rows:', rows.length);
    if (rows.length === 0) {
      return {
        processedData: [],
        parametersData: [],
        headerData: [],
        rawRows: []
      };
    }

    // Örnek satırları logla
    const sampleRows = rows.slice(0, Math.min(10, rows.length));
    console.log('Sample rows:', JSON.stringify(sampleRows));
    
    // Hangi tipler var kontrol et
    const types = [...new Set(rows.map(r => r.type))];
    console.log('Available types:', types);
    
    // Her tipten kaç tane var
    types.forEach(type => {
      const count = rows.filter(r => r.type === type).length;
      console.log(`Type ${type}: ${count} records`);
    });

    // Parameters ve Header kayıtlarını topla
    const parametersData = this.extractKeyValue(rows, 'Parameters');
    const headerData     = this.extractKeyValue(rows, 'Header');

    console.log('Parameters:', parametersData.length, 'Header:', headerData.length);
    console.log('Parameters sample:', parametersData.slice(0, 5));
    console.log('Header sample:', headerData.slice(0, 5));

    // JavaScript objelerine dönüştür (gerekirse)
    const paramObj = this.toObject(parametersData);
    const headerObj = this.toObject(headerData);
    
    console.log('Parameter object keys:', Object.keys(paramObj));
    console.log('Header object keys:', Object.keys(headerObj));

    // IbanHesap ve Details gruplarını ayır
    let ibanGroups   = this.groupByTypeAsObjects(rows, 'IbanHesap');
    let detailGroups = this.groupByTypeAsObjects(rows, 'Details');

    console.log('IbanHesap groups:', ibanGroups.length, 'Detail groups:', detailGroups.length);
    console.log('IbanHesap sample:', ibanGroups.slice(0, 2));
