  // Eksik metod implementasyonları
  private parseTypeKeyValueCsv(csv: string): any[] {
    // CSV'yi satırlara böl ve her satırı tip-key-value formatında parse et
    const lines = csv.trim().split(/\r?\n/);
    const rows: any[] = [];
    
    for (const line of lines) {
      if (line.trim()) {
        // CSV parsing - virgül ile ayrılmış ve tırnak işaretlerini handle et
        const parts = this.parseCSVLine(line);
        if (parts.length >= 3) {
          const type = parts[0].trim();
          const key = parts[1].trim();
          const value = parts.slice(2).join(',').trim(); // Eğer value'da virgül varsa birleştir
          
          rows.push({
            type: type,
            key: key,
            value: value
          });
        } else if (parts.length === 2) {
          // Bazen tip,key formatında olabilir
          rows.push({
            type: parts[0].trim(),
            key: parts[1].trim(),
            value: ''
          });
        }
      }
    }
    
    console.log('Parsed CSV rows sample:', rows.slice(0, 10));
    return rows;
  }

  // CSV satırını doğru şekilde parse et (tırnak işaretlerini de handle et)
  private parseCSVLine(line: string): string[] {
    const result: string[] = [];
    let current = '';
    let inQuotes = false;
    let i = 0;
    
    while (i < line.length) {
      const char = line[i];
      
      if (char === '"') {
        if (inQuotes && line[i + 1] === '"') {
          // Escaped quote
          current += '"';
          i += 2;
        } else {
          // Toggle quote state
          inQuotes = !inQuotes;
          i++;
        }
      } else if (char === ',' && !inQuotes) {
        result.push(current);
        current = '';
        i++;
      } else {
        current += char;
        i++;
      }
    }
    
    result.push(current); // Son field'ı ekle
    return result.map(field => field.replace(/^"|"$/g, '')); // Başında ve sonundaki tırnakları kaldır
  }

  private extractKeyValue(rows: any[], type: string): Array<{ key: string; value: string }> {
    return rows
      .filter((row: any) => row.type === type)
      .map((row: any) => ({
        key: row.key || '',
        value: row.value || ''
      }));
  }

  private toObject(keyValuePairs: Array<{ key: string; value: string }>): Record<string, string> {
    const result: Record<string, string> = {};
    for (const pair of keyValuePairs) {
      result[pair.key] = pair.value;
    }
    return result;
  }

  private groupByTypeAsObjects(rows: any[], type: string): Array<Record<string, string>> {
    const filteredRows = rows.filter((row: any) => row.type === type);
    const groups: Array<Record<string, string>> = [];
    
    if (filteredRows.length === 0) {
      return groups;
    }

    // Her unique key için bir grup oluştur ya da sequential olarak grupla
    if (type === 'Details') {
      // Details için her satırı ayrı grup olarak ele al veya ID'ye göre grupla
      let currentGroup: Record<string, string> = {};
      
      for (const row of filteredRows) {
        if (row.key && row.value !== undefined) {
          currentGroup[row.key] = row.value;
          
          // RegisterId, TotalAmount gibi son alanlar geldiğinde grubu kapat
          if (row.key.toLowerCase().includes('registerid') || 
              row.key.toLowerCase().includes('sicil') ||
              row.key.toLowerCase().includes('totalamount') ||
              row.key.toLowerCase().includes('toplam')) {
            groups.push({ ...currentGroup });
            currentGroup = {};
          }
        }
      }
      
      // Son grubu da ekle
      if (Object.keys(currentGroup).length > 0) {
        groups.push(currentGroup);
      }
    } else {
      // IbanHesap ve diğer tipler için mevcut logic
      let currentGroup: Record<string, string> = {};
      
      for (const row of filteredRows) {
        if (row.key && row.value !== undefined) {
          currentGroup[row.key] = row.value;
        }
        
        // Grup tamamlandığında yeni grup başlat
        if (Object.keys(currentGroup).length >= 2) { // IBAN ve HesapNo için minimum 2 alan
          groups.push({ ...currentGroup });
          currentGroup = {};
        }
      }
      
      // Son grubu ekle
      if (Object.keys(currentGroup).length > 0) {
        groups.push(currentGroup);
      }
    }
    
    return groups;
  }
