import { Component, EventEmitter, Output } from '@angular/core';
import { CsvOptions } from 'src/app/services/csv-converter.service';
import { TxtToJsonOptions } from 'src/app/services/txt-to-json.service';
import { FileConverterService } from 'src/app/services/file-converter.service';
import { XmlBtmService } from 'src/app/services/xml-btm.service'; // ✅ BTM servisi eklendi
import { firstValueFrom, throwError } from 'rxjs';
import { timeout, catchError } from 'rxjs/operators';

@Component({
    selector: 'app-file-uploader',
    templateUrl: './file-uploader.component.html',
    styleUrls: ['./file-uploader.component.scss'],
})
export class FileUploaderComponent {
    @Output() onConvert = new EventEmitter<any>();
    @Output() onError = new EventEmitter<string>();
    @Output() onFileClear = new EventEmitter<void>();
    @Output() onOptionsChange = new EventEmitter<any>();

    selectedFile: File | null = null;
    fileType: 'csv' | 'txt' | 'xml' | null = null;
    isProcessing = false;

    // CSV options
    hasHeader = true;
    skipEmptyLines = true;
    selectedDelimiter = ',';
    doubleQuoteWrap = true;
    selectedRowDelimiter = 'newline';
    rowPrefix = '';
    rowSuffix = '';
    selectedEncoding = 'utf-8';
    selectedQuoteOption: 'none' | 'single' | 'double' = 'none';
    trimWhitespace = true;

    // TXT options
    fieldCount = 3;
    fieldConfigs = [
        { start: 0, length: 10 },
        { start: 10, length: 10 },
        { start: 20, length: 10 },
    ];

    // XML options (UI’da varsa dursun; BTM akışında kullanılmıyor)
    xmlRootElement: string = '';
    xmlFieldMapping: string = '';

    constructor(
        private fileConverterService: FileConverterService,
        private xmlBtmService: XmlBtmService // ✅ injekte edildi
    ) { }

    // Kullanılabilir CSV ayar seçenekleri
    delimiterOptions = [
        { value: ',', label: 'Virgül (,)' },
        { value: ';', label: 'Noktalı Virgül (;)' },
        { value: '\t', label: 'Sekme (Tab)' },
        { value: '|', label: 'Dikey Çizgi (|)' },
    ];

    rowDelimiterOptions = [
        { value: 'newline', label: 'Yeni Satır (\\n)' },
        { value: 'carriage', label: 'Satır Başı (\\r)' },
        { value: '\r\n', label: 'Windows (\\r\\n)' },
    ];

    quoteOptions = [
        { value: 'none', label: 'Yok' },
        { value: 'single', label: 'Tek Tırnak (\')' },
        { value: 'double', label: 'Çift Tırnak (")' },
    ];

    encodingOptions = [
        { value: 'utf-8', label: 'UTF-8' },
        { value: 'iso-8859-1', label: 'ISO-8859-1' },
        { value: 'windows-1254', label: 'Windows-1254 (Türkçe)' },
    ];

    onFileSelect(event: any): void {
        const files = event.target.files;
        if (files && files.length > 0) {
            this.selectedFile = files[0];
        }
    }

    clearSelection(): void {
        this.selectedFile = null;
        this.fileType = null;
        const fileInput = document.getElementById('csvFileInput') as HTMLInputElement;
        if (fileInput) fileInput.value = '';
        this.onFileClear.emit();
    }

    getCsvOptions(): CsvOptions {
        return {
            hasHeader: this.hasHeader,
            skipEmptyLines: this.skipEmptyLines,
            selectedDelimiter: this.selectedDelimiter,
            doubleQuoteWrap: this.doubleQuoteWrap,
            selectedRowDelimiter: this.selectedRowDelimiter,
            rowPrefix: this.rowPrefix,
            rowSuffix: this.rowSuffix,
            selectedEncoding: this.selectedEncoding,
            selectedQuoteOption: this.selectedQuoteOption,
            trimWhitespace: this.trimWhitespace,
        };
    }

    getTxtOptions(): TxtToJsonOptions {
        return {
            fieldCount: this.fieldCount,
            startPositions: this.fieldConfigs.map(c => c.start),
            lengths: this.fieldConfigs.map(c => c.length),
            hasHeader: this.hasHeader,
            skipEmptyLines: this.skipEmptyLines,
        };
    }

    parseMapping(text: string): { [key: string]: string[] } | undefined {
        try {
            return text ? JSON.parse(text) : undefined;
        } catch {
            this.onError.emit('Field Mapping JSON formatı hatalı.');
            return undefined;
        }
    }

    async processData(): Promise<void> {
        if (!this.selectedFile || !this.fileType) {
            this.onError.emit('Dosya ve tür seçilmelidir.');
            return;
        }

        this.isProcessing = true;

        try {
            // ✅ XML DOSYASI: önce BTM'yi dene; olmazsa local parser'a düş
            if (this.fileType === 'xml') {
                const xmlText = await this.readFileAsText(this.selectedFile);

                // B projesine ham XML gönder → CSV text al (timeout ile)
                try {
                    const csvString: string = await firstValueFrom(
                        this.xmlBtmService
                            .uploadXml(xmlText)
                            .pipe(
                                timeout(2000),                         // BTM yavaşsa bekletme
                                catchError(err => throwError(() => err)) // hatayı dıştaki try/catch'e fırlat
                            )
                    );

                    // CSV → JSON (komponent içinde parse)
                    const { headers, rows } = this.parseCsvToJson(csvString);

                    // AppComponent ile uyumlu payload
                    this.onOptionsChange.emit({ via: 'btm', fileType: 'xml' });
                    this.onConvert.emit({
                        result: rows,
                        properties: headers
                    });

                    return; // XML yolunu burada bitir (BTM başarılı)
                } catch (btmErr: any) {
                    console.warn('BTM olmadı, local XML parser’a düşülüyor...', btmErr?.message || btmErr);
                }

                // 🔁 BTM başarısızsa → mevcut local converter ile devam
                const converter = this.fileConverterService.getConverter('xml');
                if (!converter) {
                    this.onError.emit('XML dönüştürücü bulunamadı.');
                    return;
                }

                const result = await converter.convert(this.selectedFile, {
                    rootElement: this.xmlRootElement || undefined,
                    fieldMapping: this.parseMapping(this.xmlFieldMapping)
                });

                this.onOptionsChange.emit({ via: 'local', fileType: 'xml' });
                this.onConvert.emit(result);
                return;
            }

            // ✅ CSV / TXT: mevcut akış olduğu gibi dursun
            const converter = this.fileConverterService.getConverter(this.fileType);
            if (!converter) {
                this.onError.emit('Desteklenmeyen dosya türü.');
                return;
            }

            let options: any = null;
            if (this.fileType === 'csv') options = this.getCsvOptions();
            else if (this.fileType === 'txt') options = this.getTxtOptions();
            else if (this.fileType === 'xml') {
                options = {
                    rootElement: this.xmlRootElement || undefined,
                    fieldMapping: this.parseMapping(this.xmlFieldMapping)
                };
            }

            const result = await converter.convert(this.selectedFile, options);
            this.onOptionsChange.emit(options);
            this.onConvert.emit(result);

        } catch (error: any) {
            this.onError.emit('Hata: ' + (error?.message || error));
        } finally {
            this.isProcessing = false;
        }
    }

    // Option triggers
    private emitOptions(): void {
        const options = this.getCsvOptions();
        this.onOptionsChange.emit(options);
    }

    onHeaderCheckboxChange(): void { this.emitOptions(); }
    onSkipEmptyLinesChange(): void { this.emitOptions(); }
    onFieldCountChange(): void {
        const count = this.fieldCount;
        while (this.fieldConfigs.length < count) {
            const last = this.fieldConfigs[this.fieldConfigs.length - 1];
            const newStart = last ? last.start + last.length : 0;
            this.fieldConfigs.push({ start: newStart, length: 10 });
        }
        while (this.fieldConfigs.length > count) {
            this.fieldConfigs.pop();
        }
    }
    onFieldConfigChange(): void { }
    onDelimiterChange(): void { this.emitOptions(); }
    onRowDelimiterChange(): void { this.emitOptions(); }
    onQuoteOptionChange(): void {
        this.doubleQuoteWrap = this.selectedQuoteOption === 'double';
        this.emitOptions();
    }
    onEncodingChange(): void { this.emitOptions(); }
    onRowPrefixChange(): void { this.emitOptions(); }
    onRowSuffixChange(): void { this.emitOptions(); }
    onTrimWhitespaceChange(): void { this.emitOptions(); }
    onDoubleQuoteWrapChange(): void {
        this.selectedQuoteOption = this.doubleQuoteWrap ? 'double' : 'none';
        this.emitOptions();
    }

    // ---------- Helpers ----------
    private readFileAsText(file: File): Promise<string> {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.onload = () => resolve(reader.result as string);
            reader.onerror = (err) => reject(err);
            reader.readAsText(file);
        });
    }

    // Basit CSV parser: ilk satır başlık, sonraki satırlar veri
    private parseCsvToJson(csv: string): { headers: string[], rows: any[] } {
        const lines = (csv || '').trim().split(/\r?\n/);
        if (lines.length === 0) return { headers: [], rows: [] };

        const headers = lines[0].split(',').map(h => h.trim());
        const rows = lines.slice(1).filter(l => l.trim() !== '').map(line => {
            const values = line.split(',').map(v => v.trim());
            const obj: any = {};
            headers.forEach((h, i) => obj[h] = (values[i] ?? ''));
            return obj;
        });

        return { headers, rows };
    }
}
<div class="file-uploader-container">
    <h3 class="text-center">Dosya Yükleme Ayarları</h3>
    <p class="text-center">Lütfen bir dosya seçin ve ayarlarınızı yapılandırın.</p>

    <!-- Dosya Seçimi ve İptal Butonu -->
    <div class="file-input-container">
        <input id="csvFileInput"
               type="file"
               class="form-control"
               (change)="onFileSelect($event)" />
        <button *ngIf="selectedFile" class="cancel-btn" (click)="clearSelection()">X</button>
    </div>

    <!-- Seçilen Dosya Durumu -->
    <div class="file-status">
        <ng-container *ngIf="!selectedFile">
            <p class="help-text">Henüz bir dosya seçilmedi.</p>
        </ng-container>
        <ng-container *ngIf="selectedFile">
            <div class="selected-file">
                <span class="file-name">{{ selectedFile.name }}</span>
                <span class="file-size">({{ (selectedFile.size / 1024).toFixed(2) }} KB)</span>
                <span class="file-type" [class.csv]="fileType === 'csv'" [class.txt]="fileType === 'txt'" [class.xml]="fileType === 'xml'">
                    {{ fileType === 'csv' ? 'CSV DOSYASI' : fileType === 'txt' ? 'TXT DOSYASI' : 'XML DOSYASI' }}
                </span>
            </div>
        </ng-container>
    </div>

    <!-- Ayarlar Bölümü -->
    <div *ngIf="selectedFile" class="options-section">
        <!-- Dosya Türü Seçimi -->
        <div class="option-item">
            <div class="option-label">
                <span class="option-text">Dosya Türü:</span>
                <select class="delimiter-select" [(ngModel)]="fileType">
                    <option [ngValue]="'csv'">CSV</option>
                    <option [ngValue]="'txt'">TXT</option>
                    <option [ngValue]="'xml'">XML</option>
                </select>
            </div>
        </div>

        <hr>

        <!-- CSV Ayarları -->
        <ng-container *ngIf="fileType === 'csv'">
            <div class="option-item">
                <label class="option-label">
                    <input type="checkbox" class="option-checkbox" [(ngModel)]="hasHeader" (change)="onHeaderCheckboxChange()" />
                    <span class="option-text">Başlık Satırı Var</span>
                </label>
            </div>

            <div class="option-item">
                <label class="option-label">
                    <input type="checkbox" class="option-checkbox" [(ngModel)]="skipEmptyLines" (change)="onSkipEmptyLinesChange()" />
                    <span class="option-text">Boş Satırları Atla</span>
                </label>
            </div>

            <div class="option-item">
                <label class="option-label">
                    <span class="option-text">Değer Ayırıcı:</span>
                    <select class="delimiter-select" [(ngModel)]="selectedDelimiter" (change)="onDelimiterChange()">
                        <option *ngFor="let option of delimiterOptions" [value]="option.value">{{ option.label }}</option>
                    </select>
                </label>
            </div>

            <div class="option-item">
                <label class="option-label">
                    <span class="option-text">Satır Sonu Ayırıcı:</span>
                    <select class="delimiter-select" [(ngModel)]="selectedRowDelimiter" (change)="onRowDelimiterChange()">
                        <option *ngFor="let option of rowDelimiterOptions" [value]="option.value">{{ option.label }}</option>
                    </select>
                </label>
            </div>

            <div class="option-item">
                <label class="option-label">
                    <span class="option-text">Tırnak İşareti Kullanımı:</span>
                    <select class="delimiter-select" [(ngModel)]="selectedQuoteOption" (change)="onQuoteOptionChange()">
                        <option *ngFor="let option of quoteOptions" [value]="option.value">{{ option.label }}</option>
                    </select>
                </label>
            </div>

            <div class="option-item">
                <label class="option-label">
                    <span class="option-text">Dosya Kodlaması:</span>
                    <select class="delimiter-select" [(ngModel)]="selectedEncoding" (change)="onEncodingChange()">
                        <option *ngFor="let option of encodingOptions" [value]="option.value">{{ option.label }}</option>
                    </select>
                </label>
            </div>

            <div class="option-item">
                <label class="option-label">
                    <input type="checkbox" class="option-checkbox" [(ngModel)]="trimWhitespace" (change)="onTrimWhitespaceChange()" />
                    <span class="option-text">Boşlukları Kırp</span>
                </label>
            </div>

            <div class="option-item">
                <label class="option-label">
                    <input type="checkbox" class="option-checkbox" [(ngModel)]="doubleQuoteWrap" (change)="onDoubleQuoteWrapChange()" />
                    <span class="option-text">Çift Tırnakla Sar</span>
                </label>
            </div>

            <div class="prefix-suffix-section">
                <div class="section-header">
                    <h4>Satır Ön/Son Eki Ayarları</h4>
                </div>
                <div class="option-item">
                    <label class="option-label">
                        <span class="option-text">Satır Ön Eki (Prefix):</span>
                        <input type="text" class="prefix-suffix-input" [(ngModel)]="rowPrefix" (input)="onRowPrefixChange()" placeholder="Örnek: #">
                    </label>
                </div>
                <div class="option-item">
                    <label class="option-label">
                        <span class="option-text">Satır Son Eki (Suffix):</span>
                        <input type="text" class="prefix-suffix-input" [(ngModel)]="rowSuffix" (input)="onRowSuffixChange()" placeholder="Örnek: EOF">
                    </label>
                </div>
            </div>

        </ng-container>

        <!-- TXT Ayarları -->
        <ng-container *ngIf="fileType === 'txt'">
            <div class="txt-options">
                <div class="option-item">
                    <label class="option-label">
                        <input type="checkbox" class="option-checkbox" [(ngModel)]="hasHeader" (change)="onHeaderCheckboxChange()" />
                        <span class="option-text">Başlık Satırı Var</span>
                    </label>
                </div>

                <div class="option-item">
                    <label class="option-label">
                        <input type="checkbox" class="option-checkbox" [(ngModel)]="skipEmptyLines" (change)="onSkipEmptyLinesChange()" />
                        <span class="option-text">Boş Satırları Atla</span>
                    </label>
                </div>

                <div class="option-item">
                    <label class="option-label">
                        <span class="option-text">Alan Sayısı:</span>
                        <input type="number" class="number-input" [(ngModel)]="fieldCount" (input)="onFieldCountChange()" min="1" />
                    </label>
                </div>
            </div>

            <div *ngIf="fieldConfigs && fieldConfigs.length > 0" class="field-settings">
                <h4>Alan Konfigürasyonları</h4>
                <div class="field-config-row">
                    <div *ngFor="let field of fieldConfigs; let i = index" class="field-config-item">
                        <label class="option-label">
                            <span class="option-text">Alan {{ i + 1 }} Başlangıç:</span>
                            <input type="number" class="number-input" [(ngModel)]="fieldConfigs[i].start" (input)="onFieldConfigChange()" />
                        </label>
                        <label class="option-label mt-2">
                            <span class="option-text">Alan {{ i + 1 }} Uzunluk:</span>
                            <input type="number" class="number-input" [(ngModel)]="fieldConfigs[i].length" (input)="onFieldConfigChange()" />
                        </label>
                    </div>
                </div>
            </div>
        </ng-container>
    </div>

    <!-- İşlem Butonları -->
    <div class="process-button-container">
        <button (click)="processData()" [disabled]="isProcessing || !selectedFile" class="process-btn">
            <span *ngIf="isProcessing" class="spinner"></span>
            Dönüştür
        </button>
        <p *ngIf="isProcessing" class="processing">İşlem devam ediyor...</p>
        <p *ngIf="!selectedFile" class="process-help-text">Lütfen bir dosya seçin.</p>
    </div>
</div>
